import "../css/Modal.css";

function Modal(props) {
  let visible = "hidden";
  if (props.modal.visible) visible = "visible";

  function handleClick() {
    props.closeModal();
  }

  function switchToEditMode() {
    props.switchToEditMode();
  }

  function handleEdit() {
    const editName = useRef(); //to get user input
    const editDescription = useRef();
    props.handleEdit(editName, editDescription, props.modal.isEditable);
  }

  return props.modal.isEditable ? (
    <div className={`modal-container ${visible}`}>
      <button onClick={handleClick} className="modal-close">
        +
      </button>
      <div className="modal-card-container">
        <div className="modal-img-wrapper">
          <img className="modal-img" src={props.modal.imgURL} />
        </div>
        <form className="modal-text-wrapper">
          <input
            ref={editName}
            type="text"
            id="card-name"
            name="cname"
            value={props.modal.name}
          />
          <input
            ref={editDescription}
            type="text"
            id="card-name"
            name="cname"
            value={props.modal.description}
          />
          <button onClick={handleEdit} className="edit-btn">
            {" "}
            Done{" "}
          </button>
        </form>
      </div>
    </div>
  ) : (
    <div className={`modal-container ${visible}`}>
      <button onClick={handleClick} className="modal-close">
        +
      </button>
      <div className="modal-card-container">
        <div className="modal-img-wrapper">
          <img className="modal-img" src={props.modal.imgURL} />
        </div>
        <div className="modal-text-wrapper">
          <h3 className="modal-name">{props.modal.name}</h3>
          <p className="modal-description">{props.modal.description}</p>
          <button onClick={switchToEditMode} className="edit-btn">
            {" "}
            Edit{" "}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Modal;
