const BuddyListReducer = (state = [], action) => {
  let buddy;
  let exist;
  switch (action.type) {
    case "ADD_BUDDY":
      buddy = action.payload;
      exist = state.find((x) => x.name === buddy.name);
      //loop through state, only update the x that x => x.name === buddy.name, return the not matching item otherwise
      if (exist)
        return state.map((x) =>
          x.name === buddy.name ? { ...exist, qty: exist.qty + 1 } : x
        );
      return [...state, { ...buddy, qty: 1 }];
    case "REMOVE_BUDDY":
      buddy = action.payload;
      exist = state.find((x) => x.name === buddy.name);
      if (exist.qty === 1)
        return [...state].filter((x) => x.name !== buddy.name);
      return state.map((x) =>
        x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
      );
    case "REMOVE_BUDDY":
      buddy = action.payload;
      exist = state.find((x) => x.name === buddy.name);
      if (exist.qty === 1)
        return [...state].filter((x) => x.name !== buddy.name);
      return state.map((x) =>
        x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
      );
    case "RESET_BUDDYLIST":
      state = [];
      return state;
    default:
      return state;
  }
};

export default BuddyListReducer;
