import axios from "axios";
const buddyListURL = "http://localhost:3000/db/buddy";

async function addBuddy(buddy) {
  return axios.post(buddyListURL + "/add", buddy);
}

const BuddyListReducer = (state = [], action) => {
  let buddy;
  let exist;
  switch (action.type) {
    case "ADD_BUDDY":
      buddy = action.payload;
      // const addNewBuddy = async () => {
      //   const response = await axios.post(buddyListURL + "/add", buddy);

      //   //optional chaining to check if response and response data both exist
      //   if (response?.data) {
      //     state = [...response.data];
      //   }
      // };
      addBuddy(buddy).then(function (response) {
        if (response?.data) {
          state = [...response.data];
        }
      });
      return;
    case "REMOVE_BUDDY":
      buddy = action.payload;
      // exist = state.find((x) => x.name === buddy.name);
      // if (exist.qty === 1)
      //   return [...state].filter((x) => x.name !== buddy.name);
      // return state.map((x) =>
      //   x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
      // );
      return;
    case "UPDATE_BUDDY":
      buddy = action.payload;
      return;
    // exist = state.find((x) => x.name === buddy.name);
    // if (exist.qty === 1)
    //   return [...state].filter((x) => x.name !== buddy.name);
    // return state.map((x) =>
    //   x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
    // );
    case "RESET_BUDDY_LIST":
      state = [];
      return state;
    case "SET_BUDDY_LIST":
      const buddyList = action.payload;
      state = [...buddyList];
      return state;
    default:
      return state;
  }
};

export default BuddyListReducer;
