const SummaryReducer = (state = { visible: false }, action) => {
  switch (action.type) {
    case "SHOW_SUMMARY":
      state = { visible: true };
      console.log(
        "%c [ state ]",
        "font-size:13px; background:pink; color:#bf2c9f;",
        state
      );
      return state;
    case "HIDE_SUMMARY":
      state = { visible: false };
      return state;
    default:
      return state;
  }
};

export default SummaryReducer;
