import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";

function Nav(props) {
  let currentUser = useRef();

  function handleUserSelect() {
    props.updateUser(currentUser.current.value);
  }
  return (
    <>
      <select
        id="userSelect"
        ref={currentUser}
        onChange={handleUserSelect}
        value={props.user}
      >
        <option value="guest">Guest</option>
        <option value="paul">Paul </option>
        <option value="michaux">Michaux</option>
      </select>
      <ul>
        <li>
          <Link to="/" className="header-links">
            Home
          </Link>
        </li>
        <li>
          <Link to="/about" className="header-links">
            About
          </Link>
        </li>
        <li>
          <Link to="/study-group" className="header-links">
            Study Group
          </Link>
        </li>
        <li>
          <Link to="/courses" className="header-links">
            Courses
          </Link>
        </li>
        <li>
          <Link to="/diagon-alley" className="header-links">
            Diagon Alley
          </Link>
        </li>
      </ul>
    </>
  );
}

export default Nav;
