const CardModalReducer = (state = [], action) => {
  let card;
  let exist;
  switch (action.type) {
    case "REFRESH_CARD_MODAL":
      list = action.payload;
      const id = modal._id;

      const temp = list.find((buddy) => buddy._id === id);
      const temp2 = { ...temp };
      temp2.visible = true;
      temp2.isEditable = false;
    case "SHOW_CARD_MODAL":
      state = { visible: true };
      return state;
    case "CLOSE_CARD_MODAL":
      state = { visible: false };
      return state;
    default:
      return state;
  }
};

export default CardModalReducer;
