const CardModalReducer = (
  state = {
    visible: false,
    isEditable: false,
    name: "",
    imgURL: "",
    description: "",
    _id: "",
  },
  action
) => {
  let buddy;
  let modal;
  let exist;
  switch (action.type) {
    case "UPDATE_CARD_MODAL":
      modal = action.payload;
      state.name = modal.name;
      state.description = modal.description;
      return state;
    case "SHOW_CARD_MODAL":
      state = { visible: true };
      return state;
    case "CLOSE_CARD_MODAL":
      state = { visible: false };
      return state;
    case "SWITCH_MODAL_MODE":
      state.isEditable = !state.isEditable;
    case "SET_CARD_MODAL":
      buddy = action.payload;
      state.visible = true;
      state.isEditable = false;
      state.name = buddy.name;
      state.imgURL = buddy.imgURL;
      state.description = buddy.description;
      state._id = buddy._id;
      return state;
    default:
      return state;
  }
};

export default CardModalReducer;
