/* Shopping Cart */
export const addToCart = (equip) => {
  return {
    type: "ADD_TO_CART",
    payload: equip,
  };
};

export const removeFromCart = (equip) => {
  return {
    type: "REMOVE_FROM_CART",
    payload: equip,
  };
};

export const resetCart = () => {
  return {
    type: "RESET_CART",
  };
};

/* DiagonAlley: Equipment List */
export const setEquipmentList = (equipmentList) => {
  return {
    type: "SET_EQUIPMENT_LIST",
    payload: equipmentList,
  };
};

/* Summary */
export const showSummary = () => {
  return {
    type: "SHOW_SUMMARY",
  };
};

export const hideSummary = () => {
  return {
    type: "HIDE_SUMMARY",
  };
};

/* Study Group */
export const addCard = (card) => {
  return {
    type: "ADD_BUDDY",
    payload: card,
  };
};

export const removeBuddy = (card) => {
  return {
    type: "REMOVE_BUDDY",
    payload: card,
  };
};

export const setBuddyList = (buddyList) => {
  return {
    type: "SET_BUDDY_LIST",
    payload: buddyList,
  };
};
