import "./App.css";
import "./css/Nav.css";
import React, { useState, useRef, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Topbar from "./components/Topbar.js";
import MobileNav from "./components/MobileNav.js";
import DesktopNav from "./components/DesktopNav.js";
import Home from "./components/Home.js";
import About from "./components/About.js";
import StudyGroup from "./components/StudyGroup.js";
import Courses from "./components/Courses.js";
import DiagonAlley from "./components/DiagonAlley.js";

function App() {
  const [user, setUser] = useState();

  useEffect(() => {
    let currentUser = localStorage.getItem("user");
    if (currentUser) {
      setUser(currentUser);
      console.log("[ has ]");
    } else {
      console.log("[ else ]");
      setUser("guest");
    }
    console.log(
      "%c [ currentUser ]",
      "font-size:13px; background:pink; color:#bf2c9f;",
      currentUser
    );

    console.log("[ user ]", user);
  }, []);

  function updateUser(user) {
    console.log(
      "%c [ user ]",
      "font-size:13px; background:pink; color:#bf2c9f;",
      user
    );
    localStorage.setItem("user", user);
    setUser(user);
  }

  return (
    <div className="App">
      <Router>
        <Topbar />
        <DesktopNav updateUser={updateUser} />
        <MobileNav updateUser={updateUser} />
        <Switch>
          <Route path="/" exact component={Home}></Route>
          <Route path="/about" component={About}></Route>
          <Route path="/study-group" component={StudyGroup}></Route>
          <Route path="/courses" component={Courses}></Route>
          <Route path="/diagon-alley" component={DiagonAlley}></Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
