import axios from "axios";

const buddyListURL = "http://localhost:3000/db/buddy";
const initialBuddylist = async () => {
  const response = await axios.get(buddyListURL);

  if (response?.data) return response.data;
};

const BuddyListReducer = (state = [], action) => {
  let buddy;
  let exist;
  switch (action.type) {
    case "ADD_BUDDY":
      buddy = action.payload;

    // return [...state, { ...buddy, qty: 1 }];
    case "REMOVE_BUDDY":
      buddy = action.payload;
    // exist = state.find((x) => x.name === buddy.name);
    // if (exist.qty === 1)
    //   return [...state].filter((x) => x.name !== buddy.name);
    // return state.map((x) =>
    //   x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
    // );
    case "UPDATE_BUDDY":
      buddy = action.payload;
    // exist = state.find((x) => x.name === buddy.name);
    // if (exist.qty === 1)
    //   return [...state].filter((x) => x.name !== buddy.name);
    // return state.map((x) =>
    //   x.name === buddy.name ? { ...exist, qty: exist.qty - 1 } : x
    // );
    case "RESET_BUDDY_LIST":
      state = [];
      return state;
    case "SET_BUDDY_LIST":
      const buddyList = action.payload;
      state = [...buddyList];
      return state;
    default:
      return state;
  }
};

export default BuddyListReducer;
