sourcesContent:
const ShoppingCartReducer = (state = [], action) => {
  const equip = action.payload;
  console.log(
    "%c [ equip ]",
    "font-size:13px; background:pink; color:#bf2c9f;",
    equip
  );
  const exist = state.find((x) => x.name === equip.name);
  switch (action.type) {
    case "ADD_TO_CART":
      //loop through state, only update the x that x => x.name === equip.name, return the not matching item otherwise
      if (exist)
        return state.map((x) =>
          x.name === equip.name ? { ...exist, qty: exist.qty + 1 } : x
        );
      return [...state, { ...equip, qty: 1 }];
    case "REMOVE_FROM_CART":
      if (exist.qty === 1)
        return [...state].filter((x) => x.name !== equip.name);
      return state.map((x) =>
        x.name === equip.name ? { ...exist, qty: exist.qty - 1 } : x
      );
    case "RESET_CART":
      state = [];
      return state;
    default:
      console.log(
        "%c [ state ]",
        "font-size:13px; background:pink; color:#bf2c9f;",
        state
      );
      return state;
  }
};

export default ShoppingCartReducer;
